import { createSlice } from "@reduxjs/toolkit";

export const todoSlice = createSlice({
    name : "todo",
    initialState: {
        value : [],
    },
    reducers : {
        addItem : (state,action) =>{
            state.valuepush(action.payload);
        },
    },
})

export const {addItem} = todoSlice.actions; 
export default todoSlice.reducer;
