import React, {useState} from 'react';
import { deleteItem } from '../redux/slice/todoSlice';
import { useDispatch } from 'react-redux';

const TodoItem = (props) => {

    const [isActive,setIsActive] = useState(false);
    const dispatch = useDispatch();

    function complete(){
        setIsActive(true);
    }

    return(
        <div className='todo-item'>
            <p className={isActive?"completed-tast":""} >{props.dat}</p>
            <button className onClick={complete} >Compelete</button>
            <button onClick={()=>dispatch(deleteItem(props.index))} >Delete</button>
        </div>
    )
}

export default TodoItem;
